const express = require('express');
const nodemailer = require('nodemailer');
const bodyParser = require('body-parser');

const app = express();
const PORT = 3000;

app.use(bodyParser.json());

// Set up Nodemailer transporter
const transporter = nodemailer.createTransport({
    host: 'smtp.gmail.com', // Replace with your SMTP server
    port: 587, // Replace with your SMTP port
    secure: false, // true for 465, false for other ports
    auth: {
        user: 'your-email@example.com', // Your email
        pass: 'your-email-password', // Your email password or app password
    },
});

app.post('/sendEmail', (req, res) => {
    const { userDetails, cartItems } = req.body;

    const mailOptions = {
        from: '"Your Company" <your-email@example.com>', // Sender address
        to: userDetails.email, // Recipient address
        subject: 'Order Confirmation', // Subject line
        text: `Hello ${userDetails.firstName},\n\nThank you for your order!\n\nYour order details:\n${JSON.stringify(cartItems)}\n\nBest regards,\nYour Company`, // Plain text body
        html: `<b>Hello ${userDetails.firstName},</b><br><p>Thank you for your order!</p><p>Your order details:</p><pre>${JSON.stringify(cartItems, null, 2)}</pre><p>Best regards,<br>Your Company</p>`, // HTML body
    };

    transporter.sendMail(mailOptions, (error, info) => {
        if (error) {
            return res.status(500).json({ message: 'Email sending failed', error });
        }
        res.json({ message: 'Order placed successfully! A confirmation email has been sent.' });
    });
});

app.listen(PORT, () => {
    console.log(`Server is running on http://localhost:${PORT}`);
});